// Custom Scroll
$(function() {
	$('.customScroll').slimScroll({
		height: "300px",
		color: '#dedfe8',
		alwaysVisible: false,
		size: "7px",
		distance: '1px',
		railVisible: false,
		railColor: "#5c36b1",
	});
});

